import React, { Component } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Login from './Admin/Login';
import Body from './Admin/js/Body';
import Menu1 from './Admin/js/Menu1';
import { postLogin } from '../host/config';

export default class App extends Component {
  state={
    token:''
  }
  handleSubmit = e => {
    e.preventDefault();
    const token ={ 
      password:this.password,
      username:this.username
    };
    postLogin(token).then(res=>{
      console.log(res.data)
      localStorage.setItem('token',res.data.key)
this.setState({token:res.data.key})
    });
  } 

   componentDidMount(){

    localStorage.clear()
  }
render() {
return (
  <div>
    {this.state.token?(<Menu1/>):(
    <div className="login-wrapper"><div className='art'>
    <h3>Please Log In</h3>
    <form  className="form1" >
    {/* onSubmit={()=>this.handleSubmit} */}
      <label>
        <span>Username</span><br/>
        <input type="text" onChange={e=>this.username=e.target.value} />
      </label><br/><br/>
      <label>
        <span>Password</span><br/>
        <input type="password" onChange={e=>this.password=e.target.value} />
      </label>
      <div><br/>
      {/* <Link to="/tush1232" onClick={()=>this.handleSubmit}>kirish</Link> */}
     <button onClick={this.handleSubmit}><a  href="http://localhost:3000/" >kirish</a> </button>
    {/* <input type="submit" value='kirish'/> */}
      </div>
    </form></div>
  </div>
    )}   


    </div>
    )
  }
}
