442\u0430 \u044D\u0442\u043E \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u0443\u0436\u0435 \u0438 \u043D\u0435 \u0442\u0430\u043A \u0438\u043D\u0442\u0435\u0440\u0435\u0441\u043D\u043E, \u0442\u043E \u0434\u043B\u044F \u043C\u043E\u043B\u043E\u0434\u0435\u0436\u0438, \u0430 \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E \u0434\u0435\u0442\u0435\u0439, \u0432\u0441\u0435 \u044D\u0442\u043E \u044F\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u043E\u0447\u0435\u043D\u044C \u0432\u0430\u0436\u043D\u044B\u043C \u0438 \u043F\u0440\u043E\u0441\u0442\u043E \u043E\u0431\u044F\u0437\u0430\u0442\u0435\u043B\u044C\u043D\u044B\u043C. \u0418\u0437\u0432\u0435\u0441\u0442\u043D\u043E, \u0447\u0442\u043E \u0441\u0430\u043C\u044B\u0435 \u0431\u043E\u043B\u044C\u0448\u0438\u0435 \u0437\u0430\u0440\u0430\u0431\u043E\u0442\u043A\u0438 \u043D\u0430 \u043F\u043E\u0441\u0442\u0441\u043E\u0432\u0435\u0442\u0441\u043A\u043E\u043C \u043F\u0440\u043E\u0441\u0442\u0440\u0430\u043D\u0441\u0442\u0432\u0435 \u0438\u043C\u0435\u044E\u0442 \u043F\u0440\u043E\u0433\u0440\u0430\u043C\u043C\u0438\u0441\u0442\u044B. \u0420\u0430\u0431\u043E\u0442\u0430\u044F \u0432 \u0431\u043E\u043B\u044C\u0448\u0438\u043D\u0441\u0442\u0432\u0435 \u0441\u0432\u043E\u0435\u043C \u043D\u0430 \u0437\u0430\u043F\u0430\u0434\u043D\u044B\u0435 \u043A\u043E\u043C\u043F\u0430\u043D\u0438\u0438, \u043E\u043D\u0438 \u0438\u043C\u0435\u044E\u0442 \u0434\u0430\u0436\u0435 \u043F\u043E 3 000 \u2013 5 000 $ \u0432 \u043C\u0435\u0441\u044F\u0446, \u0447\u0442\u043E \u044F\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u043F\u0440\u043E\u0441\u0442\u043E \u0433\u0440\u043E\u043C\u0430\u0434\u043D\u044B\u043C \u0440\u0430\u0437\u0440\u044B\u0432\u043E\u043C \u043F\u043E \u043E\u0442\u043D\u043E\u0448\u0435\u043D\u0438\u0435 \u043A \u0434\u0440\u0443\u0433\u0438\u043C \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u044C\u043D\u043E\u0441\u0442\u044F\u043C. \u041F\u043E\u043D\u044F\u0442\u043D\u043E, \u0447\u0442\u043E \u0432\u0441